{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyP2X0zYwQiHpmarSIapewAY"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":1,"metadata":{"colab":{"base_uri":"https://localhost:8080/","height":868},"id":"ajFbYIhIObej","executionInfo":{"status":"ok","timestamp":1734801605044,"user_tz":-120,"elapsed":16143,"user":{"displayName":"Ярослав Білотіл","userId":"06797585363548962278"}},"outputId":"aaaee594-f6e8-4af6-f564-2edc4411aec2"},"outputs":[{"output_type":"stream","name":"stdout","text":["Accuracy: 0.7556\n","Precision: 0.8333\n","Recall: 0.7556\n","F1 Score: 0.7503\n","Cohen Kappa Score: 0.6431\n","Matthews Corrcoef: 0.6831\n","\t\tClassification Report:\n","               precision    recall  f1-score   support\n","\n","           0       1.00      1.00      1.00        16\n","           1       0.89      0.44      0.59        18\n","           2       0.50      0.91      0.65        11\n","\n","    accuracy                           0.76        45\n","   macro avg       0.80      0.78      0.75        45\n","weighted avg       0.83      0.76      0.75        45\n","\n"]},{"output_type":"display_data","data":{"text/plain":["<Figure size 800x600 with 1 Axes>"],"image/png":"\n"},"metadata":{}}],"source":["import numpy as np\n","from sklearn.datasets import load_iris\n","from sklearn.linear_model import RidgeClassifier\n","from sklearn.model_selection import train_test_split\n","from sklearn import metrics\n","from sklearn.metrics import confusion_matrix\n","import seaborn as sns\n","import matplotlib.pyplot as plt\n","from io import BytesIO  # Потрібно для збереження SVG\n","\n","# Завантаження набору даних Iris\n","iris = load_iris()\n","X, y = iris.data, iris.target\n","\n","# Розділення даних на тренувальну та тестову вибірки\n","Xtrain, Xtest, ytrain, ytest = train_test_split(X, y, test_size=0.3, random_state=0)\n","\n","# Ініціалізація класифікатора Ridge\n","clf = RidgeClassifier(tol=1e-2, solver=\"sag\")\n","clf.fit(Xtrain, ytrain)\n","\n","# Передбачення\n","ypred = clf.predict(Xtest)\n","\n","# Обчислення метрик якості\n","print('Accuracy:', np.round(metrics.accuracy_score(ytest, ypred), 4))\n","print('Precision:', np.round(metrics.precision_score(ytest, ypred, average='weighted'), 4))\n","print('Recall:', np.round(metrics.recall_score(ytest, ypred, average='weighted'), 4))\n","print('F1 Score:', np.round(metrics.f1_score(ytest, ypred, average='weighted'), 4))\n","print('Cohen Kappa Score:', np.round(metrics.cohen_kappa_score(ytest, ypred), 4))\n","print('Matthews Corrcoef:', np.round(metrics.matthews_corrcoef(ytest, ypred), 4))\n","print('\\t\\tClassification Report:\\n', metrics.classification_report(ytest, ypred))\n","\n","# Побудова матриці плутанини\n","mat = confusion_matrix(ytest, ypred)\n","sns.set()\n","plt.figure(figsize=(8, 6))\n","sns.heatmap(mat, square=True, annot=True, fmt='d', cbar=False, cmap='Blues')\n","plt.xlabel('Predicted label')\n","plt.ylabel('True label')\n","plt.title('Confusion Matrix')\n","plt.savefig(\"Confusion.jpg\")  # Збереження матриці у форматі JPG\n","\n","# Збереження у SVG\n","f = BytesIO()\n","plt.savefig(f, format=\"svg\")\n","plt.show()\n"]}]}